import axios from "axios";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
// import { AuthContext } from "../components/auth/AuthProvider";


export default function Login() {
    // const navigate = useNavigate();
    // const { loginSuccess } = useContext(AuthContext);

    // create state to store form data
    const [formData, setFormData] = useState({});

    const handleFormChange = (e, fieldName) => {
        setFormData({ ...formData, [fieldName]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        console.log(formData);

        // try {
        //     const response = await axios.post(
        //         'http://localhost:8000/api/users/login',
        //         formData
        //     );
        //     const token = response.data.token;
        //     localStorage.setItem('token', token); // Save the token to localStorage
        //     loginSuccess(token);
        // } catch (error) {
        //     console.log(error);
        // }
    };

    return (
        <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>ID</Form.Label>
                <Form.Control type="string" placeholder="Enter staff ID" onChange={(e) => { handleFormChange(e, 'id'); }} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" name="password" onChange={(e) => { handleFormChange(e, 'password'); }} />
            </Form.Group>
            <Button variant="primary" type="submit">
                Submit
            </Button>
        </Form>
    );
}
