import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from './auth/AuthProvider';
import { Link } from "react-router-dom";

function Inventory() {
    const { user, isAuthenticated } = useAuth();
    const [inventoryData, setinventoryData] = useState([]);

    const token = localStorage.getItem('token');

    // Define the user roles that are allowed to access the form
    const allowedRoles = "ic"; // Adjust based on your roles

    // Check if the user's role is allowed to access the form

    const [formData, setFormData] = useState({
        material_Id: '',
        batch_number: "",
        expiry_date: "",
        quantity_unrestricted: "",
        qunatity_blocked: "",
    });

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleFormSubmit = async (event) => {
        event.preventDefault();

        console.log(formData);

        console.log(token);

        try {
            const response = await axios.post(
                'http://localhost:8000/api/v1/materialmaster/',
                {
                    material_description: formData.material_description,
                    buom: formData.buom,
                    price: formData.price,

                    material_Id: formData.material_Id,
                    batch_number: formData.batch_number,
                    expiry_date: formData.expiry_date,
                    quantity_unrestricted: formData.quantity_unrestricted,
                    qunatity_blocked: formData.qunatity_blocked,
                },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            // Handle the response here (e.g., show success message)
        } catch (error) {
            console.error('Creation error:', error);
            // Handle the error here (e.g., display an error message to the user)
        }


        setFormData({
            material_Id: '',
            batch_number: "",
            expiry_date: "",
            quantity_unrestricted: "",
            qunatity_blocked: "",
        });
        // Fetch updated user data to refresh the user table;
        axios.get("http://localhost:8000/api/v1/materialmaster/")
            .then(response => {
                setinventoryData(response.data);
            })
            .catch(error => {
                console.error("Error fetching material data:", error);
            });
    };




    // Table data containing user information (example)
    useEffect(() => {
        axios.get("http://localhost:8000/api/v1/inventory/")
            .then(response => {
                setinventoryData(response.data);
            })
            .catch(error => {
                console.error("Error fetching user data:", error);
            });
    }, []);

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };
    const blockInventory = async (inventory) => {
        console.log(token);
        console.log(inventory);
        try {
            const response = await axios.patch(
                'http://localhost:8000/api/v1/inventory/block', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },

                data: {
                    materialId: inventory.material_Id,
                    batchNumber: parseInt(inventory.batch_number),
                },
            }
            );

            // Handle the response here (e.g., show a success message)
        } catch (error) {
            console.error('Block error:', error);
            // Handle the error here (e.g., display an error message to the user)
        }
        axios.get("http://localhost:8000/api/v1/inventory/")
            .then(response => {
                setinventoryData(response.data);
            })
            .catch(error => {
                console.error("Error fetching user data:", error);
            });
    };

    const unblockInventory = async (inventory) => {
        console.log(token);
        console.log(inventory);
        try {
            const response = await axios.patch(
                'http://localhost:8000/api/v1/inventory/unblock', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },

                data: {
                    materialId: inventory.material_Id,
                    batchNumber: parseInt(inventory.batch_number),
                },
            }
            );

            // Handle the response here (e.g., show a success message)
        } catch (error) {
            console.error('Unblock error:', error);
            // Handle the error here (e.g., display an error message to the user)
        }
        axios.get("http://localhost:8000/api/v1/inventory/")
            .then(response => {
                setinventoryData(response.data);
            })
            .catch(error => {
                console.error("Error fetching user data:", error);
            });
    };

    const deleteInventory = async (inventory) => {
        console.log(token);
        console.log(inventory);
        try {
            const response = await axios.delete(
                'http://localhost:8000/api/v1/inventory/delete', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },

                data: {
                    materialId: inventory.material_Id,
                    batchNumber: parseInt(inventory.batch_number),
                },
            }
            );

            // Handle the response here (e.g., show a success message)
        } catch (error) {
            console.error('Deletion error:', error);
            // Handle the error here (e.g., display an error message to the user)
        }
        axios.get("http://localhost:8000/api/v1/inventory/")
            .then(response => {
                setinventoryData(response.data);
            })
            .catch(error => {
                console.error("Error fetching user data:", error);
            });
    };

    return (
        <div>
            {/* {isAuthenticated() && allowedRoles === user.role && ( */}
            {true && (
                <div id="material-div" className="mb-4">
                    {/* Display the form for authorized users */}
                    <h2>Add material master</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="materialId" className="col-form-label">Material ID</label>
                                    <select
                                        id="materialId"
                                        name="materialId"
                                        className="form-control"
                                        value={formData.materialId}
                                        onChange={handleInputChange}
                                    >
                                        <option value="">Select Material ID</option>
                                        {materialIds.map(id => (
                                            <option key={id} value={id}>
                                                {id}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="expiryDate" className="col-form-label">Expiry Date</label>
                                    <input
                                        type="date"
                                        id="expiryDate"
                                        name="expiryDate"
                                        className="form-control"
                                        value={formData.expiryDate}
                                        onChange={handleInputChange}
                                    />
                                </div>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="quantityUnrestricted" className="col-form-label">Quantity Unrestricted</label>
                                    <input
                                        type="number"
                                        id="quantityUnrestricted"
                                        name="quantityUnrestricted"
                                        className="form-control"
                                        value={formData.quantityUnrestricted}
                                        onChange={handleInputChange}
                                    />
                                </div>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="quantityBlocked" className="col-form-label">Quantity Blocked</label>
                                    <input
                                        type="number"
                                        id="quantityBlocked"
                                        name="quantityBlocked"
                                        className="form-control"
                                        value={formData.quantityBlocked}
                                        onChange={handleInputChange}
                                    />
                                </div>
                            </div>
                        </div>

                        <div className="row">
                            <div className="col-md-6">
                                <button type="submit" className="btn btn-primary">Submit</button>
                            </div>
                        </div>
                    </form>

                </div >
            )
            }

            <div>
                <h2>Inventory Table</h2>
                <table className="table table-hover">
                    <thead>
                        <tr>
                            <th>Material ID</th>
                            <th>Batch Number</th>
                            <th>Expiry Date</th>
                            <th>Quantity Unrestricted</th>
                            <th>Quantity Blocked</th>
                        </tr>
                    </thead>
                    <tbody>
                        {inventoryData.map((inventory, index) => (
                            <tr key={index}>
                                <td>{inventory.material_Id}</td>
                                <td>{inventory.batch_number}</td>
                                <td>{formatDate(inventory.expiry_date)}</td> {/* Format the expiry date as needed */}
                                <td>{inventory.quantity_unrestricted}</td>
                                <td>{inventory.quantity_blocked}</td>
                                <td>
                                    <button className="btn btn-primary" onClick={() => blockInventory(inventory)}>Block</button>
                                    <button className="btn btn-success" onClick={() => unblockInventory(inventory)}>Unblock</button>
                                    <button className="btn btn-danger" onClick={() => deleteInventory(inventory)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div >
    );
};

export default Inventory;
