import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from './auth/AuthProvider';

function Users() {
    const { user } = useAuth();
    const [usersData, setUsersData] = useState([]);

    // Define the user roles that are allowed to access the form
    const allowedRoles = ["admin"]; // Adjust based on your roles

    // Check if the user's role is allowed to access the form
    // const canAccessForm = allowedRoles.includes(user.role);

    // Table data containing user information (example)
    useEffect(() => {
        axios.get("http://localhost:8000/api/v1/users/")
            .then(response => {
                setUsersData(response.data);
            })
            .catch(error => {
                console.error("Error fetching user data:", error);
            });
    }, []);

    return (
        <div>
            {true && (
                <div
                    id="login-div"
                    className="position-absolute top-50 start-50 translate-middle"
                    style={{ width: "50vh" }}
                >
                    <form id="login-form" onSubmit={handleFormSubmit}>
                        <h1 id="login-header" className="h3 mb-3 fw-normal">
                            Login
                        </h1>
                        <div id="id-div" className="form-floating">
                            <input
                                type="text"
                                className="form-control"
                                id="id-input"
                                name="id"
                                placeholder="id"
                                value={formData.id}
                                onChange={handleInputChange}
                            />
                            <label htmlFor="id-input">ID</label>
                        </div>
                        <div id="password-div" className="form-floating">
                            <input
                                type="password"
                                className="form-control"
                                id="password-input"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                            />
                            <label htmlFor="password-input">Password</label>
                        </div>
                        <button
                            id="login-button"
                            className="btn btn-primary w-100 py-2"
                            type="submit"
                        >
                            Sign in
                        </button>
                    </form>
                </div>
            )}

            <h2>User Table</h2>
            <table className="table table-hover">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Email</th>
                        <th>Role</th>
                    </tr>
                </thead>
                <tbody>
                    {usersData.map((userData) => (
                        <tr key={userData.id}>
                            <td>{userData.id}</td>
                            <td>{userData.email}</td>
                            <td>{userData.role}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Users;
