import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from './auth/AuthProvider';

function Users() {
    const { user } = useAuth();
    const [usersData, setUsersData] = useState([]);

    // Define the user roles that are allowed to access the form
    const allowedRoles = ["admin"]; // Adjust based on your roles

    // Check if the user's role is allowed to access the form
    // const canAccessForm = allowedRoles.includes(user.role);

    // Table data containing user information (example)
    useEffect(() => {
        axios.get("http://localhost:8000/api/v1/users/")
            .then(response => {
                setUsersData(response.data);
            })
            .catch(error => {
                console.error("Error fetching user data:", error);
            });
    }, []);

    return (
        <div>
            {true && (
                <div>
                    {/* Display the form for authorized users */}
                    <h2>User Form</h2>
                    {/* Add your form elements here */}
                </div>
            )}

            <h2>User Table</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Email</th>
                        <th>Role</th>
                    </tr>
                </thead>
                <tbody>
                    {usersData.map((userData) => (
                        <tr key={userData.id}>
                            <td>{userData.id}</td>
                            <td>{userData.email}</td>
                            <td>{userData.role}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Users;
