import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from './auth/AuthProvider';

function Users() {
    const { user } = useAuth();
    const [usersData, setUsersData] = useState([]);

    // Define the user roles that are allowed to access the form
    const allowedRoles = ["admin"]; // Adjust based on your roles

    // Check if the user's role is allowed to access the form
    // const canAccessForm = allowedRoles.includes(user.role);

    // Table data containing user information (example)
    useEffect(() => {
        axios.get("http://localhost:8000/api/v1/users/")
            .then(response => {
                setUsersData(response.data);
            })
            .catch(error => {
                console.error("Error fetching user data:", error);
            });
    }, []);

    return (
        <div>
            {true && (
                <div id="login-div" className="mb-4">
                    {/* Display the form for authorized users */}
                    <h2>Add users</h2>
                    <form id="login-form">
                        <div className="row">
                            <div className="col-md-2">
                                <div className="form-floating">
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="id-input"
                                        name="first_name"
                                        placeholder="First Name"
                                    // value={formData.id}
                                    // onChange={(e) =>
                                    //     setFormData({ ...formData, id: e.target.value })
                                    // }
                                    />
                                    <label htmlFor="id-input">First Name</label>
                                </div>
                            </div>
                            <div className="col-md-2">
                                <div className="form-floating">
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="id-input"
                                        name="last_name"
                                        placeholder="Last Name"
                                    // value={formData.id}
                                    // onChange={(e) =>
                                    //     setFormData({ ...formData, id: e.target.value })
                                    // }
                                    />
                                    <label htmlFor="id-input">Last Name</label>
                                </div>
                            </div>

                            <div className="col-md-2">
                                <div className="form-floating">
                                    <input
                                        type="email"
                                        className="form-control"
                                        id="id-input"
                                        name="email_address"
                                        placeholder="Email Address"
                                    // value={formData.id}
                                    // onChange={(e) =>
                                    //     setFormData({ ...formData, id: e.target.value })
                                    // }
                                    />
                                    <label htmlFor="id-input">Email Address</label>
                                </div>
                            </div>

                            <div className="col-md-2">
                                <div className="form-floating">
                                    <input
                                        type="password"
                                        className="form-control"
                                        id="id-input"
                                        name="password"
                                        placeholder="password"
                                    // value={formData.id}
                                    // onChange={(e) =>
                                    //     setFormData({ ...formData, id: e.target.value })
                                    // }
                                    />
                                    <label htmlFor="id-input">Password</label>
                                </div>
                            </div>

                            <div className="col-md-2">
                                <div className="form-floating">
                                    <select
                                        className="form-select"
                                        id="role-select"
                                        name="role"
                                        value={formData.role}
                                        onChange={(e) =>
                                            setFormData({ ...formData, role: e.target.value })
                                        }
                                    >
                                        <option value="">Select Role</option>
                                        <option value="master">Master</option>
                                        <option value="mm">MM</option>
                                        <option value="ic">IC</option>
                                    </select>
                                    <label htmlFor="role-select">Role</label>
                                </div>
                            </div>
                            <div className="col-md-2 d-flex justify-content-end">
                                <button
                                    id="Submit-button"
                                    className="btn btn-primary"
                                    type="submit"
                                >
                                    Sign in
                                </button>

                            </div>
                            {/* Add more columns for other form fields */}
                        </div>

                    </form>
                    {/* Add your form elements here */}
                </div >
            )
            }

            <h2>User Table</h2>
            <table className="table table-hover">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Email</th>
                        <th>Role</th>
                    </tr>
                </thead>
                <tbody>
                    {usersData.map((userData) => (
                        <tr key={userData.id}>
                            <td>{userData.id}</td>
                            <td>{userData.email}</td>
                            <td>{userData.role}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div >
    );
}

export default Users;
